/**
 * Get platform icon by platform name
 * @param platform - Platform name (case-insensitive)
 * @returns SVG icon string or default icon
 */
export function getPlatformIcon(platform: string | null | undefined): string {
    if (!platform) {
        return platformIcons.default;
    }

    const normalizedPlatform = platform.toLowerCase().trim();

    // Check for exact match
    if (platformIcons[normalizedPlatform]) {
        return platformIcons[normalizedPlatform];
    }

    // Return default icon
    return platformIcons.default;
}

